package javawithsba;

public class MaxMinTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.printf("byte 타입의 최댓값 = %d", Byte.MAX_VALUE).println();
		System.out.printf("byte 타입의 최솟값 = %d", Byte.MIN_VALUE).println();
		System.out.println("short 타입의 최솟값 = " + Short.MIN_VALUE);
		System.out.println("short 타입의 최솟값 = " + Short.MIN_VALUE);
		System.out.printf("int 타입의 최솟값 = %d", Integer.MIN_VALUE).println();
		System.out.printf("int 타입의 최솟값 = %d", Integer.MIN_VALUE).println();
		System.out.println("long 타입의 최솟값 = " + Long.MIN_VALUE);
		System.out.println("long 타입의 최솟값 = " + Long.MIN_VALUE);
		System.out.printf("float 타입의 최댓값 = " + Float.MAX_VALUE).println();
		System.out.printf("float 타입의 최솟값 = " + Float.MIN_VALUE).println();
		System.out.printf("double 타입의 최댓값 = " + Double.MAX_VALUE).println();
		System.out.printf("double 타입의 최솟값 = " + Double.MIN_VALUE).println();
	}

}